import React, { Component } from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min';
import $ from 'jquery';
import Card from './Card';

class Event extends Component {
    constructor() {
        super()
        this.state = {
            acara: [
                {
                    nama: "Gerakan Nasional Pilah Sampah", tanggal: "8 Juli 2022", deskripsi:"",

                    cover: "data:image/jpeg;base64,"
                },
                {
                    nama: "Lomba Poster", tanggal: "10 Mei 2022", deskripsi:"",

                    cover: "data:image/jpeg;base64,"

                },
                {

                    nama: "Kerja Bakti", tanggal: "27 Agustus 2022", deskripsi:"",

                    cover: "data:image/jpeg;base64,"

                },
                {

                    nama: "Reboisasi Toga", tanggal: "18 April 2022", deskripsi:"",

                    cover: "data:image/jpeg;base64,"

                },
            ],
            action: "",
            nama: "",
            tanggal: "",
            deskripsi: "",
            selectedItem: null,
        }
        this.state.filterAcara = this.state.acara
    }
    render() {
        return (
            <div className="container">
                <div className="row">
                    <input type="text" className="form-control my-2" placeholder="Pencarian" value={this.state.keyword} onChange={ev => this.setState({ keyword: ev.target.value })} onKeyUp={ev => this.searching(ev)} />
                    {this.state.filterAcara.map((item, index) => (
                        <Card
                            nama={item.nama}
                            tanggal={item.tanggal}
                            deskripsi={item.deskripsi}
                            cover={item.cover}
                            onEdit={() => this.Edit(item)}
                            onDrop={() => this.Drop(item)}
                        />
                    ))}
                </div>

                <br/>
                <button className="btn btn-success" onClick={() => this.Add()}>
                    Tambah Data
                </button>

                {/* component modal sbg control manipulasi data */}
                <div className="modal" id="modal_acara">
                    <div className="modal-dialog">
                        <div className="modal-content">
                            {/* modal header */}
                            <div className="modal-header">
                                Form Acara
                            </div>
                            {/* modal body */}
                            <div className="modal-body">
                                <form onSubmit={ev => this.Save(ev)}>
                                    Nama Acara
                                    <input type="text" className="form-control mb-2"

                                        value={this.state.nama}
                                        onChange={ev => this.setState({
                                            nama:
                                                ev.target.value
                                        })}
                                        required />

                                    Tanggal Acara
                                    <input type="text" className="form-control mb-2"

                                        value={this.state.tanggal}
                                        onChange={ev => this.setState({
                                            tanggal
                                                : ev.target.value
                                        })}
                                        required />

                                    Deskripsi Acara
                                    <input type="text" className="form-control mb-2"

                                        value={this.state.deskripsi}
                                        onChange={ev => this.setState({
                                            deskripsi:
                                                ev.target.value
                                        })}
                                        required />

                                    Cover Acara
                                    <input type="url" className="form-control mb-2"

                                        value={this.state.cover}
                                        onChange={ev => this.setState({
                                            cover:
                                                ev.target.value
                                        })}
                                        required />

                                    <button className="btn btn-success btn-block" type="submit">
                                        Simpan
                                    </button>
                                </form>
                            </div>
                        </div>
                    </div>
                </div>
            </div>

        )
    }
    Add = () => {
        // menampilkan komponen modal
        $("#modal_acara").show()
        this.setState({
            isbn: Math.random(1, 10000000),

            nama: "",
            tanggal: "",
            deskripsi: "",
            cover: "",
            action: "insert"
        })
    }
    Edit = (item) => {
        // menampilkan komponen modal
        $("#modal_acara").show()
        this.setState({
            nama: item.nama,
            tanggal: item.tanggal,
            deskripsi: item.deskripsi,
            cover: item.cover,
            action: "update",
            selectedItem: item
        })
    }
    Save = (event) => {
        event.preventDefault();
        // menampung data state acara
        let tempAcara = this.state.acara
        if (this.state.action === "insert") {
            // menambah data baru
            tempAcara.push({
                nama: this.state.nama,
                tanggal: this.state.tanggal,
                deskripsi: this.state.deskripsi,
                cover: this.state.cover,
            })

        } else if (this.state.action === "update") {
            // menyimpan perubahan data
            let index = tempAcara.indexOf(this.state.selectedItem)
            tempAcara[index].nama = this.state.nama
            tempAcara[index].tanggal = this.state.tanggal
            tempAcara[index].deskripsi = this.state.deskripsi
            tempAcara[index].cover = this.state.cover
        }
        this.setState({ acara: tempAcara })
        // menutup komponen modal_acara
        $("#modal_acara").hide()
    }
    Drop = (item) => {
        // beri konfirmasi untuk menghapus data
        if (window.confirm("Apakah anda yakin ingin menghapus data ini?")) {
            // menghapus data
            let tempAcara = this.state.acara
            // posisi index data yg akan dihapus
            let index = tempAcara.indexOf(item)
            // hapus data
            tempAcara.splice(index, 1)
            this.setState({ Acara: tempAcara })
        }
    }
    searching = event => {
        if (event.keyCode === 13) {
            // 13 adalah kode untuk tombol enter
            let keyword = this.state.keyword.toLowerCase()
            let tempAcara = this.state.Acara
            let result = tempAcara.filter(item => {
                return item.nama.toLowerCase().includes(keyword) ||
                    item.tanggal.toLowerCase().includes(keyword) ||
                    item.deskripsi.toLowerCase().includes(keyword)
            })
            this.setState({ filterAcara: result })
        }
    }

}

export default Event;